#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_matrix_dimensions() {
        assert_eq!(BLUE_NOISE_64.len(), 64, "Matrix should have 64 rows");
        for (i, row) in BLUE_NOISE_64.iter().enumerate() {
            assert_eq!(row.len(), 64, "Row {} should have 64 columns", i);
        }
    }

    #[test]
    fn test_matrix_value_range() {
        // u8 guarantees 0-255 range, so just verify we have actual values
        // by checking the matrix is populated (not all zeros)
        let mut has_nonzero = false;
        for row in BLUE_NOISE_64.iter() {
            for &value in row.iter() {
                if value > 0 {
                    has_nonzero = true;
                    break;
                }
            }
        }
        assert!(has_nonzero, "Matrix should contain non-zero values");
    }

    #[test]
    fn test_matrix_has_variety() {
        // Count unique values - a good blue noise matrix should have many unique values
        let mut values = std::collections::HashSet::new();
        for row in BLUE_NOISE_64.iter() {
            for &value in row.iter() {
                values.insert(value);
            }
        }

        // A 64x64 matrix has 4096 values. With 256 possible values,
        // a good blue noise texture should use most of them.
        assert!(
            values.len() > 200,
            "Matrix should have >200 unique values, found {}",
            values.len()
        );
    }

    #[test]
    fn test_matrix_distribution() {
        // Check that the values are reasonably distributed (not clustered)
        let mut sum: u64 = 0;
        let mut count: u64 = 0;

        for row in BLUE_NOISE_64.iter() {
            for &value in row.iter() {
                sum += value as u64;
                count += 1;
            }
        }

        let mean = sum as f64 / count as f64;
        // Mean should be roughly 127.5 for uniform distribution
        assert!(
            (mean - 127.5).abs() < 15.0,
            "Mean {} is too far from expected 127.5",
            mean
        );
    }

    #[test]
    fn test_total_elements() {
        let total: usize = BLUE_NOISE_64.iter().map(|row| row.len()).sum();
        assert_eq!(total, 4096, "Matrix should have 4096 total elements");
    }
}
